mapper.select:
SELECT blog_no, blog_nick, blog_img_no
FROM blog
WHERE member_no = #{member_no}

SELECT blog_title, blog_id, blog_nick, blog_img_no 
FROM blog 
WHERE member_no = #{member_no}

mapper.update:
update blog 
SET blog_title = #{blog_title},
	blog_nick = #{blog_nick},
	blog_info = #{blog_info},
	blog_topic = #{blog_topic}
where blog_id = #{blog_id}

mapper.insert:
INSERT INTO blog_visit (blog_no, blog_object_no, visitor_no, date

	, query

)
VALUES (#{blog_no}, #{blog_object_no}, #{visitor_no}, SYSDATE()

	, #{query}

)

mapper.select:
SELECT category_name
FROM blog_category
WHERE blog_category_no = #{blog_category_no}

SELECT neighbor_member_no 
FROM blog_neighbor 
WHERE member_no = #{member_no}

SELECT member_no 
FROM blog_neighbor
WHERE neighbor_member_no = #{member_no}

SELECT COUNT(blog_object_no) FROM blog_object AS O, blog_category AS C WHERE
O.blog_no = #{no}
O.blog_category_no = #{no}
AND O.is_public = 0 AND C.is_public = 0
AND O.blog_category_no = C.blog_category_no

SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM, TB.* FROM (SELECT blog_object_no, title, DATE, O.is_public, hits FROM blog_object AS O, blog_category AS C WHERE
O.blog_no = #{no}
O.blog_category_no = #{no}
AND C.is_public = 0 AND O.is_public = 0
AND O.blog_category_no = C.blog_category_no ORDER BY blog_object_no DESC) AS TB , (SELECT @ROWNUM := 0) AS RN LIMIT #{startNo}, #{endNo}

SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM, TB.* FROM (SELECT blog_object_no, O.blog_category_no, category_name, title, content, date, O.is_public, hits, likes FROM blog_object AS O, blog_category AS C WHERE
O.blog_no = #{no}
O.blog_category_no = #{no}
AND C.is_public = 0 AND O.is_public = 0
AND O.blog_category_no = C.blog_category_no ORDER BY blog_object_no DESC) AS TB , (SELECT @ROWNUM := 0) AS RN LIMIT #{startNo}, #{endNo}

SELECT * FROM blog_object 
WHERE blog_no = #{blog_no}
	AND blog_object_no = #{blog_object_no}

		AND is_public = 0
